import React, { FC, useState } from "react";
import { Box, Text, Flex, Button } from '@chakra-ui/react';
import { mintNFTContract } from "../contracts";

interface MainProps{
    account: string;
}

const Main: FC<MainProps> = ({account}) => {
    const [newImage, setNewImage] = useState<string>();

    const onClickMint =async () => {
        try{
            if(!account) return;

            const response = await mintNFTContract.methods.mintNFTtoken().send({from:account});
            console.log(response);
        }catch (error){
            console.error(error);
        }
    };

    return (
    <Flex w="full" h="100vh" justifyContent="center" alignItems="center" direction="column">
        <Box>
            {newImage ? (
                <div>NFT</div>
            ) : (
                <Text>LET'S MINT NFT!!</Text>
            )}
        </Box>
        <Button onClick={onClickMint} >
        MINT</Button>
    </Flex>
    );
 };
export default Main;
